mapper.select:
        SELECT SUM(car_rental_deposit) FROM contract_info c 

	        1 = 1 


	        	and c.companyId = #{companyId}


		        and c.create_date >= #{beginDateStr} 



		        and c.create_date <= #{endDateStr}

        SELECT SUM(violation_deposit) FROM contract_info c 

	        1 = 1 


	        	and c.companyId = #{companyId}


		        and c.create_date >= #{beginDateStr} 



		        and c.create_date <= #{endDateStr}
